import { useState, useEffect } from "react"
import { updateArtist } from "../../services/artists.js"
import "./Profile.css"

function Profile(props) {
  const { currentUser, setCurrentUser } = props
  const [profileData, setProfileData] = useState({
    name: "",
    artist_statement: "",
    image_url: "",
  })

  useEffect(() => {
    if (currentUser) {
      setProfileData({
        name: currentUser.name,
        artist_statement: currentUser.artist_statement,
        image_url: currentUser.image_url,
      })
    }
  }, [])

  const handleSubmit = async (event) => {
    event.preventDefault()
    const artist = await updateArtist(currentUser.id, profileData)
    setCurrentUser(artist)
  }

  const handleChange = (event) => {
    setProfileData({
      ...profileData,
      [event.target.name]: event.target.value,
    })
  }

  return(
    <div>
      <form onSubmit={handleSubmit} autoComplete="off">
        <label htmlFor="name">name:</label>
        <div className="input">
          <input 
            name="name"
            type="text"
            value={formData.name}
            placeholder="name"
            onChange={handleChange}
          />
        </div>
        <div className="input">
          <label htmlFor="artist_statement">artist's statement:</label>
          <textarea
            name="artist_statement"
            value={formData.artist_statement}
            onChange={handleChange}
          />
        </div>
        <div className="input">
          <label htmlFor="image_url">profile image URL:</label>
          <input
            name="image_url"
            type="text"
            value={formData.image_url}
            placeholder="URL for Profile Pic"
            onChange={handleChange}
          />
        </div>
      </form>
    </div>
  )
}
export default Profile
