import { useState, useEffect } from "react"
import { useHistory, Link } from "react-router-dom"
import { signout } from "../../services/artists.js"
import { checkForShow } from "../../services/shows.js"
import "./Nav.css"

function Nav(props) {
  const [showID, setShowID] = useState(null)
  const history = useHistory()
  const { currentUser, setCurrentUser } = props

  useEffect(() => {
    if (currentUser) {
    const checkShow = async (artistID) => {
      const show = await checkForShow(artistID)
      if (show) setShowID(show.id)
    }
    checkShow(currentUser.id)}
    }, [currentUser])

  const handleClick = async () => {
    await signout
    setCurrentUser(null)
    history.push("/")
  }

  const userNavBar = (
    <div className="user-nav-bar">
      <p>{`Hello, ${currentUser?.username}`}</p>
      <Link to="/shows">shows</Link>
      {showID ? 
        <Link to={`/shows/${showID}/edit`}>edit show</Link>
        :
        <Link to="/create-show">create show</Link>
      }
      <button className="signout-button" onClick={handleClick}>sign out</button>
    </div>
  )

  const defaultNavBar = (
    <div className="default-nav-bar">
      <Link to="/shows" >shows</Link>
      <Link to="/artists" >sign up</Link>
      <Link to="/login" >sign in</Link>
    </div>
  )

  return(
    <div className="nav">
      <Link to="/">ArtSpace</Link>
      { currentUser ? userNavBar : defaultNavBar }
    </div>
  )
}

export default Nav
