import api from "./apiConfig"

export const getWork = async (show_id, work_id) => {
  try {
    const res = await api.get(`/shows/${show_id}/works/${work_id}`)
    return res.data
  } catch (error) {
    throw error
  }
}

export const editWork = async (show_id, work_id, workData) => {
  try {
    const res = await api.put(`/shows/${show_id}/works/${work_id}`, workData)
    return res.data
  } catch (error) {
  throw error
  }
}

export const createWork = async (show_id, workData) => {
  try {
    const res = await api.post(`/shows/${show_id}/works`, workData)
    return res.data
  } catch (error) {
    throw error
  }
}
